// Smallest Lexicographically Equivalent Letter = SLEL
class Solution {
    private class UnionFind {
        private int[] parentArr;    // stores the SLEL for all 26 characters
        private UnionFind(int n) {
            parentArr = new int[n]; // initialise the parent array
            // // By default, each letter itself is it's SLEL
            for (int i = 0; i < n; i++) {
                parentArr[i] = i;
            }
        }

        // this method would fetch us the SLEL for a particular character
        private int getParent(int i) {
            // example : SLEL of k is k, so we return k
            // but SLEL of s is r, again we see SLEL of r is k 
            // (recursive call getParent(parentArr[i]) will keep setting SLELs of our character at index i) till we
            // reach the situation "each letter itself is it's SLE" is encountered (parentArr[i] == i)
            // character k has been set as the SLEL of r and s and we return k when k/r/s is passed
            return (parentArr[i] == i)? i : (parentArr[i] = getParent(parentArr[i])); 
        }

        // this method would set the SLELs for each corresponding characters in s1 and s2
        private void union(int i, int j) {
            int parent1 = getParent(i); // get the SLEL for char at index i
            int parent2 = getParent(j); // get the SLEL for char at index i
            if (parent1 < parent2) {    // if SLEL at i < SLEL at j
                // SLEL at i now becomes SLEL at j too 
                parentArr[parent2] = parent1;   
            } else {    // SLEL at i > SLEL at j
                // SLEL at j now becomes SLEL at i too 
                parentArr[parent1] = parent2;   
            }
        }
    }

    public String smallestEquivalentString(String s1, String s2, String baseStr) {
        UnionFind uf = new UnionFind(26);   // initialises our UnionFind object
        for (int i = 0; i < s1.length(); i++) {
            int c1 = s1.charAt(i) - 'a';
            int c2 = s2.charAt(i) - 'a';
            uf.union(c1, c2);   // this will map the SLEL to c1 as well as SLEL to c2 in the parent array
        }

        StringBuilder sb = new StringBuilder(); // to build our answer string
        for (int i = 0; i < baseStr.length(); i++) {
            // this would fetch us the SLEL for our current character of baseStr
            int smallestMappedChar = uf.getParent(baseStr.charAt(i) - 'a');
            sb.append((char) (smallestMappedChar + 'a'));   // we will append it to our answer
        }

        return sb.toString();   // return the answer string
    }
}

// TC: O(len(s1) + len(baseStr)), SC: O(1)
