class Solution {
public:
    vector<int> parent;
    // return the ultimate parent (representative) of the component containing x as one of its element.
    int find(int x){
        if(parent[x]==x)    return x;

        return parent[x] = find(parent[x]);
    }
    // Perform union if a and b aren't in the same component.
    void _union(int a , int b){
        int x = find(a);
        int y = find(b);
        // return if a and b are in the same component
        if(x==y)    return;             
        // Make the smaller character parent of the other one as we want the lexicograohically smallest string.
        if(x > y)   parent[x] = y;
        else        parent[y] = x;
    }
    string smallestEquivalentString(string s1, string s2, string baseStr) {
        parent.resize(26);
        // Make each character parent of itself.
        for(int i=0 ; i<26 ; i++){
            parent[i]=i;
        }
        // Perform union merge for all the pairs of characters from both the strings s1 and s2.
        for(int i=0 ; i<s1.size() ; i++){
            _union(s1[i]-'a' , s2[i]-'a');
        }
        // Create the answer string with final parent vector.
        string res="";
        for(char ch : baseStr){
            res += (char)(find(ch-'a')+'a');
        }
        return res;
    }
};
