class Solution {
public:
    void refineMap(map<char, set<char>> &mp, int times) {
        while(times--) {
            for(auto [k, v]: mp)
                for(char i: v) mp[k].insert(*mp[i].begin());
        }
    }

    string smallestEquivalentString(string s1, string s2, string baseStr) {
        map<char, set<char>> mp;
        int n = s1.size(); string res = "";

        for(int i = 0; i < n; i++)
            mp[s1[i]].insert(s2[i]);

        for(int i = 0; i < n; i++) {
            if(mp.find(s1[i]) != mp.end())
                for(char x: mp[s1[i]])
                    mp[s2[i]].insert(x);
            mp[s2[i]].insert(s1[i]);
        } refineMap(mp, 2);

        for(char i: baseStr) {
            if(mp.find(i) != mp.end()) res += *mp[i].begin();
            else res += i;
        } return res;
    }
};
