class DisjointSet:
    def __init__(self):
        self.parent = {}
        # intitial every one if parent of themselves
        # making self as parent initial
        for letter in string.ascii_lowercase:
            self.parent[letter] = letter

    def findParent(self,node):
            while  node!= self.parent[node]:
                node = self.parent[node]

            return node

    def doUnion(self, a,b):
        # find ultimate parent of a and b
        ulp_a = self.findParent(a)
        ulp_b = self.findParent(b)

        # assign minimum as ultimate parent of both a and b
        self.parent[ulp_a] = self.parent[ulp_b] = min(ulp_a, ulp_b)


class Solution:
    def smallestEquivalentString(self, s1: str, s2: str, baseStr: str) -> str:
        ds = DisjointSet()
        for i in range(len(s1)):
            ds.doUnion(s1[i], s2[i])

        ans = ""
        for c in baseStr:
            ans += ds.parent[ds.findParent(c)]
        return ans

```

