class Solution:
    def smallestEquivalentString(self, s1: str, s2: str, baseStr: str) -> str:
        graph = defaultdict(set)
        for p, q in zip(s1, s2):
            graph[p].add(q)
            graph[q].add(p)

        answer = []
        for char in baseStr:
            visited = set()
            answer.append(dfs(char, graph, visited))
        return "".join(answer)

def dfs(node, graph, visited):
    if node in visited:
        return node

    visited.add(node)
    letter = node

    for n in graph[node]:
        letter = min(letter, dfs(n, graph, visited))
    return letter
