class Solution {
    struct DSU
    {
        vector<int> siz,parent;
        void init()
        {
            siz.resize(26);
            parent.resize(26);
            for(int i=0;i<26;i++)
            {
                siz[i]=1;
                parent[i]=i;
            }
        }
        int leader(int ex)
        {
            if(ex==parent[ex])
                return ex;
            return parent[ex]=leader(parent[ex]);
        }
        void merge(int a,int b)
        {
            a=leader(a);
            b=leader(b);
            if(a==b)
                return;
            if(siz[a]<siz[b])
                swap(a,b);
            siz[a]+=siz[b];
            parent[b]=parent[a];
        }
    };
public:
    string smallestEquivalentString(string s1, string s2, string baseStr) {
        DSU d;
        d.init();
        for(int i=0;i<s1.size();i++)
            d.merge(s1[i]-'a',s2[i]-'a');
        vector<char> represent(26,'z');
        for(int i=0;i<26;i++)
            represent[d.leader(i)]=std::min(represent[d.leader(i)],(char)('a'+i));
        string ans="";
        for(auto ele : baseStr)
            ans+=represent[d.leader(ele-'a')];
        return ans;
    }
};
```

