class Solution {
    func smallestEquivalentString(_ s1: String, _ s2: String, _ baseStr: String) -> String {

        // Create adjacency list 
        var neighbors: [Character: Set<Character>] = [:]

        // Fill adj list
        for (c1, c2) in zip(s1, s2) {
            neighbors[c1, default: []].insert(c2)
            neighbors[c2, default: []].insert(c1)
        }

        // Memoize Characters
        var memo = [Character: Character]()

        // BFS character to get the lexicographically smallest equivalent character 
        func bfs(_ char: Character) -> Character {
            guard memo[char] == nil else { return memo[char]! }    
            var res = char
            var seen = Set<Character>()
            var queue = [Character]([char])
            while queue.count > 0 {
                let c = queue.removeFirst() 
                if seen.contains(c) {
                    continue
                } else {
                    seen.insert(c)
                }

                // result is the minimum of res and any character the bfs path can lead to
                res = min(res, c)

                for neighbor in neighbors[c] ?? [] {
                    queue.append(neighbor)
                }

            }
            // This is the path of a specific character. Therefore all characters in this path are equivalent by transitivity.
            // Memo all seen characters with the resulting character (AKA the lowest lexicograpgic character)
            for v in seen {
                memo[v] = res
            }
            return res
        }

        // Get the result in string format to return
        var res = ""
        for c in baseStr {
            res += String(bfs(c))
        }

        return res
    }
}
