class Solution {
    public String smallestEquivalentString(String s1, String s2, String baseStr) {
        int i, N = s1.length();

        char[] parent = new char[26];
        for(i = 0; i < 26; i++){
            parent[i] = (char)('a' + i);
        }

        for(i = 0; i < N; i++){
            union(s1.charAt(i), s2.charAt(i), parent);
        }

        StringBuilder builder = new StringBuilder();
        N = baseStr.length();

        for(i = 0; i < N; i++){
            builder.append(find(baseStr.charAt(i), parent));
        }

        return builder.toString();
    }

    private void union(char x, char y, char[] parent){
        char p1 = find(x, parent);
        char p2 = find(y, parent);

        if(p1 != p2){
            if(p1 < p2)
                parent[p2 - 'a'] = p1;
            else
                parent[p1 - 'a'] = p2;
        }
    }

    private char find(char ch, char[] parent){
        char x = ch;

        while(x != parent[x - 'a']){
            x = parent[x - 'a'];
        }

        parent[ch - 'a'] = x;
        return x;
    }
}
