class UF {
    int[] parent;
    int[] min;

    public UF() {
        parent = new int[26];
        min = new int[26];
        for (int i = 0; i < 26; i++) {
            parent[i] = i;
            min[i] = i;
        }
    }

    public int find(int x, int mi) {
        if (parent[x] == x) {
            min[x] = Math.min(min[x], mi);
            return x;
        }
        return parent[x] = find(parent[x], Math.min(parent[x], mi));
    }

    public void union(int x, int y) {
        int px = find(x, x);
        int py = find(y, y);
        parent[px] = py;
        min[py] = Math.min(min[py], min[px]);
    }
}

class Solution {
    public String smallestEquivalentString(String A, String B, String S) {
        UF uf = new UF();
        int n = A.length();
        for (int i = 0; i < n; i++) {
            uf.union(A.charAt(i) - 'a', B.charAt(i) - 'a');
        }
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < S.length(); i++) {
            char rep = (char)(uf.min[uf.find(S.charAt(i) - 'a', S.charAt(i) - 'a')] + 'a');
            sb.append(rep);
        }
        return sb.toString();
    }
}
