        HashMap<Character, List<Character>> map = new HashMap<>();

        // making the graph using hashmap, can also use array of size 26 and djacency list for map
        for(int i =0;i<s1.length();i++)
        {
            char u = s1.charAt(i);
            char v = s2.charAt(i);
         if(map.get(u) == null)
         {
             map.put(u, new ArrayList<>());
         }
         if(map.get(v) == null)
         {
             map.put(v, new ArrayList<>());
         }
            if(u!=v)// just to avoid repeating characters in graph
            {
                 map.get(u).add(v);
               map.get(v).add(u);
            }

        }

        // string builder is imp or will give TLE 
        StringBuilder sb = new StringBuilder();
        // memo hasmap to optimize time and space 
       HashMap<Character, Character> memo = new HashMap<>();
        // calling the dfs on every character of baseStr
        for(int i=0;i<baseStr.length();i++)
        {
             char currChar = baseStr.charAt(i);
            // every time new visited array is created or will hav to backtrack if dedclared outside the for loop
             boolean visited[]  = new boolean[26];
            // will get the max on very dfs run 
            char max [] = new char[1];
            max[0] = currChar;
            // we apply the memo hashmap in this for loop and not in the dfs function (if we implement in dfs function
            // lot of tcs will fail )
            if(memo.get(currChar)!=null){
                 sb.append(memo.get(currChar));
                continue;
            }
            // calling recursive function 
            dfs(map, baseStr, currChar, max, visited);
            // store the max in memo w.r.t to currChar in memo map
            memo.put(currChar, max[0]);
            // make the answer 
            sb.append(max[0]);
        }

        return sb.toString();

    }
    // simple DFS function
    public void dfs( HashMap<Character,List<Character>> map, String baseStr, char ch, char [] max, boolean [] visited)
    {
        if(map.get(ch)==null)
            return;

        visited[ch -'a'] = true;

          for(char child : map.get(ch) )
          {

              if(visited[child-'a'] == false)
              {
                   dfs(map, baseStr,child,max, visited ); 
                  // comparing the max value
                  // can be before the dfs call or after makes no difference in this question
                if(child<max[0])
                {
                        max[0] = child;
                }

              }

          }

    }
}

