# Approach
Maintain a map of minHeaps && iteratively updating minHeap as it sees data.

# Complexity
- Time complexity:

- Space complexity:
O(n^2)

# Code
```
/**
 * @param {string} s1
 * @param {string} s2
 * @param {string} baseStr
 * @return {string}
 */
var smallestEquivalentString = function(s1, s2, baseStr) {
    let map = {};
    for(let i = 0; i < s1.length; i++) {
        let p = s1[i];
        let q = s2[i];
        let pInMap = p in map;
        let qInMap = q in map;
        if(!pInMap && !qInMap) {
            let heap = new MinHeap();
            map[p] = heap;
            map[q] = heap;
            heap.insert(p);
            heap.insert(q);
        } else if(pInMap && qInMap && map[p] !== map[q]){
            for(let m of map[q].data) {
                !map[p].has(m) && map[p].insert(m);
                map[m] = map[p];
            }
            map[q] = map[p]; 
        } else {
            let heap = map[p] || map[q];
            if(!pInMap) {
                heap.insert(p);
                map[p] = heap;
            } else {
                heap.insert(q);
                map[q] = heap;
            }
        }
    }
    let s = "";
    for(let b of baseStr) {
        s += map[b]?.peekMin() ?? b;
    }
    return s;
};

function MinHeap() {
  this.data = [];
}

MinHeap.prototype.insert = function(val) {
  this.data.push(val);
  this.bubbleUp(this.data.length-1);
};

MinHeap.prototype.bubbleUp = function(index) {
  while (index > 0) {
    // get the parent
    var parent = Math.floor((index + 1) / 2) - 1;

    // if parent is greater than child
    if (this.data[parent] > this.data[index]) {
      // swap
      var temp = this.data[parent];
      this.data[parent] = this.data[index];
      this.data[index] = temp;
    }

    index = parent;
  }
};

MinHeap.prototype.has = function(element) {
    let d = new Set(this.data);
    return d.has(element);
}

MinHeap.prototype.peekMin = function() {
  return this.data[0];
};

MinHeap.prototype.extractMin = function() {
  var min = this.data[0];

  // set first element to last element
  this.data[0] = this.data.pop();

  // call bubble down
  this.bubbleDown(0);

  return min;
};

MinHeap.prototype.bubbleDown = function(index) {
  while (true) {
    var child = (index+1)*2;
    var sibling = child - 1;
    var toSwap = null;

    // if current is greater than child
    if (this.data[index] > this.data[child]) {
      toSwap = child;
    }

    // if sibling is smaller than child, but also smaller than current
    if (this.data[index] > this.data[sibling] && (this.data[child] == null || (this.data[child] !== null && this.data[sibling] < this.data[child]))) {
        toSwap = sibling;
    }

    // if we don't need to swap, then break.
    if (toSwap == null) {
      break;
    }

    var temp = this.data[toSwap];
    this.data[toSwap] = this.data[index];
    this.data[index] = temp;

    index = toSwap;
  }
};
