            arr[i] = 'a' + i;

        for (int i=0; i<s1.size(); i++) {
            char toReplace = max(arr[s1[i]-'a'], arr[s2[i]-'a']);
            char replaceWith = min(arr[s1[i]-'a'], arr[s2[i]-'a']);
            for (int i=0; i<26; i++)
                if (arr[i] == toReplace)
                    arr[i] = replaceWith;
        }

        for (int i = 0; i<baseStr.size(); i++)
            baseStr[i] = arr[baseStr[i]-'a'];

        return baseStr;
    }
};
