class union_find {
public:
    vector<char> parent;

    union_find() {
        parent.resize(26);

        for (int i=0; i<26; i++)
            parent[i] = char(i + 'a');
    }

    char find_parent(char c) {
        if (parent[c - 'a'] == c)
            return c;

        return parent[c - 'a'] = find_parent(parent[c - 'a']);
    }

    void merge(char a, char b) {
        int parA = find_parent(a);
        int parB = find_parent(b);

        if (parA < parB)
            parent[parB - 'a'] = parA;
        else 
            parent[parA - 'a'] = parB;
    }
};

class Solution {
public:
    string smallestEquivalentString(string s1, string s2, string baseStr) {
        union_find uf;
        int n = s1.size();

        for (int i=0; i<n; i++) 
            uf.merge(s1[i], s2[i]);

        for (int i=0; i<baseStr.size(); i++) 
            baseStr[i] = uf.find_parent(baseStr[i]);

        return baseStr;
    }
};
