class Solution {
    public String smallestEquivalentString(String s1, String s2, String baseStr) {
        UnionFind uf = new UnionFind();
        for(int i = 0; i < s1.length(); i++){
            uf.union(s1.charAt(i) - 'a', s2.charAt(i) - 'a');
        }
        StringBuilder sb = new StringBuilder();
        for(char c : baseStr.toCharArray()){
            int res = uf.min(c - 'a');
            char curr = (char)(res + 'a');
            sb.append(curr);
        }
        return sb.toString();
    }

    class UnionFind{
        int[] root;
        int[] rank;
        //int size;
        public UnionFind(){
            root = new int[26];
            rank = new int[26];
            for(int i = 0; i < 26; i++){
                rank[i] = 1;
                root[i] = i;
            }
        }
        public int find(int x) {
        if (x == root[x]) {
            return x;
        }
        return root[x] = find(root[x]);
        }

        public int min(int x){
            int rootX = find(x);
            for(int i = 0; i < 26; i++){
                if(root[i] == rootX){
                    return i;
                }
            }
            return 0;
        }
        public void union(int x, int y){
            int rootX = find(x);
            int rootY = find(y);
            if(rootX != rootY){
                if(rank[rootX] > rank[rootY]){
                    root[rootY] = rootX;
                }
                else if(rank[rootY] > rank[rootX]){
                    root[rootX] = rootY;
                }
                else{
                    root[rootY] = rootX;
                    rank[rootX]++;
                }
            }
        }
    }
}
