class Solution {
public:
    string smallestEquivalentString(string s1, string s2, string baseStr) {
        int n = s1.size();
        vector<vector<int>> graph(26);
        for(int i = 0 ; i < n ; i++){
            graph[s1[i] - 'a'].push_back(s2[i] - 'a');
            graph[s2[i] - 'a'].push_back(s1[i] - 'a');
        }

        string res;

        vector<int> marker(26,-1);

        function<void(int , vector<int> , int)> dfs = [&](int node , vector<int> visited , int val){
            visited[node] = 1;
            marker[node] = val;
            for(auto &child : graph[node]){
                if(visited[child] != 1){
                    dfs(child , visited , val);
                }
            }
        };

        for(int i = 0 ; i < s1.size(); i++){
            vector<int> visited(26,0);
            int val = s1[i] - 'a';
            if(marker[val] == -1) dfs(val , visited , val);
        }

        for(int i = 0 ; i < baseStr.size(); i++){
            int val = marker[baseStr[i] - 'a']; 
            if(val == -1){
                res += baseStr[i];
                continue;
            }
            int ans = 27;
            for(int j = 0 ; j < 26 ; j++){
                if(marker[j] == val){
                    ans = min(ans , j);
                }
            }
            res += ans + 'a';
        }

        return res;
    }
};

