class Solution {
public:
    string smallestEquivalentString(string A, string B, string S) {

        // we have possible 26 alphabets
        int n=26;
        vector<int> representative(n);

        for (int i = 0; i < n; i++) {
            representative[i] = i;
        }

        for(int i=0;i<A.length();i++)
        {
            combine(representative,A[i]-'a',B[i]-'a');
        }

        string str = "";

        for(int i=0;i<S.length();i++)
        {
            str += (find(representative,S[i]-'a')+'a');
        }

        return str;

    }
    int find(vector<int> &representative, int vertex) {
        if (vertex == representative[vertex]) {
            return vertex;
        }

        return representative[vertex] = find(representative, representative[vertex]);
    }

