//Find the root of left cell and top cell, if they have the same root, we find the circle.
//Time complexity O(m*n*α*(h)), where α is Ackermann function, h is height of the parent tree. α*(h) is close to constant.

```
public class Solution
{
    public bool ContainsCycle(char[][] grid)
    {
        int m = grid.Length;
        int n = grid[0].Length;
        var uf = new UF(m * n);
        int[] dirs = new int[] { 0, -1, 0 };
        for (int i = 0; i < m; i++)
        {
            for (int j = 0; j < n; j++)
            {
                HashSet<int> roots = new HashSet<int>();
                for (int d = 0; d < 2; d++)
                {
                    int ni = i + dirs[d];
                    int nj = j + dirs[d + 1];
                    if (ni < 0 || nj < 0 || grid[i][j] != grid[ni][nj])
                        continue;
                    int root = uf.Find(ni * n + nj);
                    if (roots.Contains(root))
                        return true;
                    roots.Add(root);
                }
                foreach (int r in roots)
                    uf.Union(r, i * n + j);
            }
        }

        return false;
    }
}

public class UF
{
    private int[] parent;
    public UF(int n)
    {
        parent = new int[n];
        for (int i = 0; i < n; i++)
        {
            parent[i] = i;
        }
    }

    public int Find(int x)
    {
        if (x == parent[x])
            return x;
        return parent[x] = Find(parent[x]);
    }

    public void Union(int x, int y)
    {
        int px = Find(x);
        int py = Find(y);
        if(px != py)
            parent[px] = py;
    }
}
```

