class Solution {
    boolean[][] vis;
    boolean res; // Defalut false;
    public boolean containsCycle(char[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        vis = new boolean[m][n];
		// We use DFS here.
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(!res&&!vis[i][j])
                    dfs(grid, i, j,grid[i][j],0); // 0 is the default direction, which can go four directions in the next step.
            }
        }
        return res;
    }
	// Pre is to save the direction of our previous deep search, to ensure that the next step does not go in the opposite direction
    public void dfs(char[][] grid, int i, int j, char c, int pre) {
		// Pruning
        if(res||i<0||i>=grid.length||j<0||j>=grid[0].length||grid[i][j]!=c) return;
        if(!vis[i][j]){
            vis[i][j] = true;
			// Here is the key to the problem
            if(pre!=1) dfs(grid, i+1, j, c, 2);
            if(pre!=2) dfs(grid, i-1, j, c, 1);
            if(pre!=3) dfs(grid, i, j+1, c, 4);
            if(pre!=4) dfs(grid, i, j-1, c, 3);
        }else {
		// If we  reach the location where we have arrived, then there is a circle.
             res = true;
        }
    }
}
