```
class Solution {
    int X;
    int[][] visited;
    char[][] grid;
    public boolean containsCycle(char[][] grid) {
        this.X = 0; // To mark the paths in visited matrix
        this.visited = new int[grid.length][grid[0].length]; // Keep track of visited points
        this.grid = grid;

        for (int r = 0; r < grid.length; r++) {
            for (int c = 0; c < grid[r].length;c++) {
                if (visited[r][c] == 0) {
                    X++; // Increment so we don't conflict paths. 
                    // isCycle will mark all reachable nodes from our current point with X
                    if (isCycle(r,c,-1,-1, grid[r][c])) return true;                  
                }
            }
        }
        return false;
    }

    public boolean isCycle(int r, int c, int prevR, int prevC, char orig) {
        //If out of range or not the same char as original (meaning can't continue path), return false
        if (r < 0 || r >= grid.length || c < 0 || c >= grid[r].length || grid[r][c] != orig)
            return false;


        if (visited[r][c] == X) return true; // If we visited this already, that means theres a cycle.
        // We don't need to check length because we make sure we don't visit a previous one already.

        visited[r][c] = X; // All paths will now have the same X value.

        // For each one, visit the next point ONLY if its not a previous point.
        return (
            (r-1 != prevR && isCycle(r-1, c, r, c, orig)) || 
            (c+1 != prevC && isCycle(r, c+1, r, c, orig)) ||
            (r+1 != prevR && isCycle(r+1, c, r, c, orig)) ||
            (c-1 != prevC && isCycle(r, c-1, r, c, orig))
        );
    }
}
