class Solution {
public:
    bool containsCycle(vector<vector<char>>& grid) {
        int m = grid.size(), n = grid[0].size();
        if(m*n <4)
            return false;

        vector<vector<bool>> visited(m, vector<bool>(n));
        for(int i = 0; i < m; ++i)
            for(int j = 0; j < n; ++j) {
                if(dfs(i, j, i, j, grid, visited, grid[i][j], 0))
                    return true;
            }

        return false;
    }

    bool dfs(int i, int j, int sx, int sy, vector<vector<char>>& grid, vector<vector<bool>>& visited, char ch, int step) {

        if(grid[i][j] != ch || visited[i][j])
            return false;

        visited[i][j] = 1;


        for(int d = 0; d < 4; ++d) {
            int nx = i + dirs[d];
            int ny = j + dirs[d+1];
			//for the new x and new y, it cann't be the source x and source y otherwise it is not a cycle.
            if(nx < 0 || nx >= grid.size() || ny < 0 || ny >= grid[0].size() || grid[nx][ny] != ch)
                continue;
            if(nx == sx && ny == sy)
                continue;
         //   if(visited[nx][ny] && step >= 3) { don't need to check the step here, can remove from parameter list
		      if(visited[nx][ny]) {
                return true;
            }
            if(dfs(nx, ny, i, j, grid, visited, ch, step+1)) {
                return true;
            }
        }

        return false;
    }

    vector<int> dirs = {0, 1, 0, -1, 0};
};
