class Solution {
public:
    bool containsCycle(vector<vector<char>>& grid) {
        vector<vector<int>>v(grid.size(), vector<int>(grid[0].size(), 0));
        for(int i=0; i<grid.size(); i++){
            for(int j=0; j<grid[0].size(); j++){
                if(!v[i][j]){
                    v[i][j]=1;
                    char c=grid[i][j];
                    queue<pair<pair<int, int>, pair<int, int>>>q;
                    q.push({{i, j}, {-1, -1}});
                    while(!q.empty()){
                        int x=q.front().first.first, y=q.front().first.second;
                        int px=q.front().second.first, py=q.front().second.second;
                        //cout<<x<<" "<<y<<" "<<px<<" "<<py<<endl;
                        q.pop();
                        if(x<grid.size()-1 && grid[x+1][y]==c){
                            //cout<<"H "<<x+1<<" "<<y<<" "<<px<<" "<<py<<endl;
                            if(v[x+1][y] && (x+1!=px || y!=py)){return true;}
                            else if(!v[x+1][y]){
                                v[x+1][y]=1;
                                q.push({{x+1, y}, {x, y}});
                            }
                        }
                        if(x>0 && grid[x-1][y]==c){
                            //cout<<"A "<<x-1<<" "<<y<<" "<<px<<" "<<py<<endl;
                            if(v[x-1][y] && (x-1!=px || y!=py)){return true;}
                            else if(!v[x-1][y]){
                                v[x-1][y]=1;
                                q.push({{x-1, y}, {x, y}});
                            }
                        }
                        if(y>0 && grid[x][y-1]==c){
                            //cout<<"B "<<x<<" "<<y-1<<" "<<px<<" "<<py<<endl;
                            if(v[x][y-1] && (x!=px || y-1!=py)){return true;}
                            else if(!v[x][y-1]){
                                v[x][y-1]=1;
                                q.push({{x, y-1}, {x, y}});
                            }
                        }
                        if(y<grid[0].size()-1 && grid[x][y+1]==c){
                            //cout<<"C "<<x<<" "<<y+1<<" "<<px<<" "<<py<<endl;
                            if(v[x][y+1] && (x!=px || y+1!=py)){return true;}
                            else if(!v[x][y+1]){
                                v[x][y+1]=1;
                                q.push({{x, y+1}, {x, y}});
                            }
                        }
                    }
                }
            }
        }
        return false;
    }
};
