class Solution {
    vector<int> parents;
    int find_set(int v)
    {
        if(v == parents[v])
            return v;
        return parents[v] = find_set(parents[v]);
    }
    void union_set(int a, int b)
    {
        a = find_set(a);
        b = find_set(b);
        if(a == b) return;
        parents[b] = a;
        return;
    }
public:
    bool containsCycle(vector<vector<char>>& grid) {
        int n = grid.size(), m = grid[0].size();
        for(int i = 0; i < n; i++)
        {
            for(int j = 0; j < m; j++)
            {
                parents.push_back(i*m+j);
            }
        }
        for(int i = 0; i < n; i++)
        {
            for(int j = 0; j < m; j++)
            {
                if(i > 0 && grid[i][j] == grid[i-1][j])
                {
                    int x = i*m+j;
                    int y = x-m;
                    if(find_set(x) == find_set(y))
                        return true;
                    union_set(x,y);
                }
                if(j < m-1 && grid[i][j] == grid[i][j+1])
                {
                    int x = i*m+j;
                    int y = x+1;
                    if(find_set(x) == find_set(y))
                        return true;
                    union_set(x,y);
                }
            }
        }
        return false;
    }
};
