class Solution {
public:
    bool containsCycle(vector<vector<char>>& grid) {
        map<pair<int,int>, set<pair<int,int>>> visited;
        int directions[4][2] = {{-1,0},{1,0},{0,-1},{0,1}};
        deque<pair<int,int>> dq;
        for(int i=0;i<grid.size();i++){
            for (int j=0;j<grid[0].size();j++){
                if (!visited.count({i,j})){
                    visited[{i,j}] = {{0,0}};
                    dq = {{i,j}};
                    while (!dq.empty()){
                        auto [i,j] = dq.front();
                        dq.pop_front();
                        for (auto [di,dj]:directions){
                            if (!visited[{i,j}].count({-di,-dj})){
                                if (0<=i+di && i+di<grid.size() && 0<=j+dj && j+dj<grid[0].size() && grid[i+di][j+dj]==grid[i][j]){
                                    if (!visited.count({i+di,j+dj})){
                                        visited[{i+di,j+dj}] = {{di,dj}};
                                        dq.push_back({i+di,j+dj});
                                    }
                                    else
                                        return true;
                                }
                            }
                        }
                    }

                }
            }
        }
        return false;
    }
};
