class Solution {
public:
    int ans=0;
    bool fun(vector<vector<char>>&grid,int i,int j,char c)
    {
        int m=grid.size();
        int n=grid[0].size();
        if(i<0||j<0||i>=m||j>=n)
            return false;
        char upper=toupper(c);

        if(grid[i][j]!=c&&grid[i][j]!=upper)
            return false;
        if(grid[i][j]==upper)
            return true;

        grid[i][j]=upper;
        bool b1=fun(grid,i+1,j,c);
        bool b2=fun(grid,i-1,j,c);
        bool b3=fun(grid,i,j+1,c);
        bool b4=fun(grid,i,j-1,c);
        int count=0;
        if(b1)
            count++;
        if(b2)
            count++;
        if(b3)
            count++;
        if(b4)
            count++;
        ans=max(ans,count);
        return ans>=2;

    }
    bool containsCycle(vector<vector<char>>& grid)
    {
        for(int i=0;i<grid.size();i++)
        {
            for(int j=0;j<grid[i].size();j++)
            {
                if(grid[i][j]<='z'&&grid[i][j]>='a')
                {
                    if(fun(grid,i,j,grid[i][j]))
                        return true;
                }
            }
        }
        return false;
    }
};
