public:
    bool cycle(vector<vector<char>>& grid ,vector<vector<bool>> &visited, char c,int i,int j,int row, int col,int len,int parrow,int parcol)
    {
        //boundary checks
        if(i<0||j<0||i>=row||j>=col)
            return false;
        //if different character return false
        if(grid[i][j]!=c)
            return false;
        //minimum length of cycle has to be 4
        if(visited[i][j] && len>=4 && grid[i][j]==c)
            return true;
        //discard if less than 4 and node visited
        if(visited[i][j]&& len<4 && grid[i][j]==c)
            return false;
        visited[i][j] =true;
        //we use parrow and parrcol to track that the cycle is not coming from parent 
        bool  s = (cycle(grid,visited,c,i+1,j,row,col,len+1,i,j) && !( i+1 ==parrow && j==parcol)) || (cycle(grid,visited,c,i,j+1,row,col,len+1,i,j) &&!( i ==parrow && j+1==parcol))||(cycle(grid,visited,c,i-1,j,row,col,len+1,i,j) && !( i-1 ==parrow && j==parcol))||(cycle(grid,visited,c,i,j-1,row,col,len+1,i,j) &&  !( i ==parrow && j-1==parcol));
        return s;
    }

