class DisjointSet {
    private final int[] root;
    private final int[] rank;
    DisjointSet(int size) {
        root = new int[size];
        rank = new int[size];
        for (int i=0; i<size; i++) {
            root[i] = i;
            rank[i] = 1;
        }
    }

    private int find(int vertex) {
        if(root[vertex] == vertex) return vertex;
        return root[vertex] = find(root[vertex]);
    }

    public void union(int vertex1, int vertex2) {
        int root1 = find(vertex1);
        int root2 = find(vertex2);
        if(root1 != root2) {
            if(rank[root1] > rank[root2]) {
                root[root2] = root1;
            } else if(rank[root1] < rank[root2]) {
                root[root1] = root2;
            } else {
                root[root1] = root2;
                rank[root2]++;
            }
        }
    }

    public boolean isConnected(int vertex1, int vertex2) {
        return find(vertex1) == find(vertex2);
    }
}

class Solution {
    private final List<Integer[]> directions = List.of(
            new Integer[]{1, 0},
            new Integer[]{-1, 0},
            new Integer[]{0, 1},
            new Integer[]{0, -1}
    );
    public boolean containsCycle(char[][] grid) {
        Set<Integer> visited = new HashSet<>();
        int m = grid.length, n = grid[0].length;
        DisjointSet set = new DisjointSet(m*n);
        for (int i=0; i<m; i++) {
            for (int j=0; j<n; j++) {
                int cellId = i * n + j;
                if(!visited.contains(cellId)) {
                    if(dfs(i, j, grid[i][j], grid, visited, set))
                        return true;
                }
            }
        }
        return false;
    }

    private boolean dfs(int i, int j, char c, char[][] grid, Set<Integer> visited, DisjointSet set) {
        int cellId = i * grid[0].length + j;
        visited.add(cellId);
        for (Integer[] neighbour: getNeighbours(i, j, c, grid)) {
            int id = neighbour[0] * grid[0].length + neighbour[1];
            if (!visited.contains(id)) {
                if(set.isConnected(cellId, id)) return true;
                set.union(cellId, id);
            }
        }
        for (Integer[] neighbour: getNeighbours(i, j, c, grid)) {
            int id = neighbour[0] * grid[0].length + neighbour[1];
            if (!visited.contains(id))
                return dfs(neighbour[0], neighbour[1], c, grid, visited, set);
        }
        return false;
    }

    private List<Integer[]> getNeighbours(int i, int j, char c, char[][] grid) {
        List<Integer[]> neighbours = new ArrayList<>();
        for (Integer[] direction: directions) {
            int x = i + direction[0], y = j + direction[1];
            if(isValidCoordinate(grid, x, y) && grid[x][y] == c)
                neighbours.add(new Integer[]{x, y});
        }
        return neighbours;
    }

    private boolean isValidCoordinate(char[][] grid, int i, int j) {
        return i >= 0 && i < grid.length && j >= 0 && j < grid[0].length;
    }
}
