class Solution:
    def containsCycle(self, grid: List[List[str]]) -> bool:
        nrows, ncols = len(grid), len(grid[0])
        dist = []
        for _ in range(nrows):
            dist.append([None for _ in range(ncols)])

        def dfs(r, c):
            letter = grid[r][c]
            for dr, dc in ((-1, 0), (1, 0), (0, -1), (0, 1)):
                rr, cc = r + dr, c + dc
                if 0 <= rr < nrows and 0 <= cc < ncols and grid[rr][cc] == letter:
                    if dist[rr][cc] is None:
                        dist[rr][cc] = dist[r][c] + 1
                        if dfs(rr, cc):
                            return True
                    elif abs(dist[rr][cc] - dist[r][c]) >= 3:
                        return True
            return False

        for r in range(nrows):
            for c in range(ncols):
                if dist[r][c] is None:
                    dist[r][c] = 0
                    status = dfs(r, c)
                    if status:
                        return True
        return False
