class Solution {
    public boolean containsCycle(char[][] grid) {
        boolean[][] visited = new boolean[grid.length][grid[0].length];
        for(int i=0; i<grid.length; i++) {
            for(int j=0; j<grid[0].length; j++) {
                if(!visited[i][j] && isCycleDetected(grid, i, j, visited, i, j)) {
                    return true;
                }
            }
        }
        return false;
    }

    public boolean isCycleDetected(char[][] grid, int x, int y, 
                              boolean[][] visited, int prevX, int prevY) {
        visited[x][y] = true;
        int[][] directions = {{0,1},{1,0},{0,-1},{-1,0}};
        for(int[] dir : directions) {
            int newX = x+dir[0];
            int newY = y+dir[1];
            if(isSafe(grid, newX, newY, visited, prevX, prevY)) {
                if(visited[newX][newY]) {
                    return true;
                } else {
                    if(isCycleDetected(grid,newX, newY,visited,x, y)) {
                        return true;
                    }
                }
            }
        }
        return false;
    }

    public boolean isSafe(char[][] grid, int x, int y, 
                          boolean[][] visited, int prevX, int prevY) {
        if(x < 0 || x >= grid.length || y<0 || y >= grid[x].length 
           || (x==prevX && y==prevY) || grid[x][y] != grid[prevX][prevY]) {
            return false;
        }
        return true;
    }
}
