class Solution {
    boolean[][] visited;
    public boolean containsCycle(char[][] grid) {

        visited = new boolean[grid.length][grid[0].length];

        for(int i = 0; i < grid.length; i++)
            for(int j = 0; j < grid[0].length; j++)
                if(!visited[i][j] && check(grid, i, j, -1, -1))
                    return true;

        return false;
    }

    int[] x = {1, -1, 0, 0};
    int[] y = {0, 0, -1, 1};

    //DFS
    private boolean check(char[][] grid, int row, int col, int lastRow, int lastCol) {

        if(visited[row][col]) return false;
        visited[row][col] = true;

        for(int i = 0; i < 4; i++){
            int newRow = row + x[i];
            int newCol = col + y[i];

            // if index is in range and the character is also same then only in that case check further
            if(isSafe(grid, newRow, newCol) && grid[row][col] == grid[newRow][newCol]){

                // if we encountered the already visited index and it is not come from the previous Call then we have found the cycle 
                if(visited[newRow][newCol] && newRow != lastRow && newCol != lastCol) return true;
                // otherwise do dfs
                if(check(grid, newRow, newCol, row, col)) return true;
            }
        }

        return false;
    }

    private boolean isSafe(char[][] grid, int i, int j) {
        return 0 <= i && i < grid.length && 0 <= j && j < grid[0].length; 
    }
}
