class DisjointSets:
    def __init__(self):
        self.parent = dict()
    def makeSet(self, u):
        self.parent[u] = u
    def find(self, u):
        if u not in self.parent:
            self.parent[u] = u
            return u
        elif u != self.parent[u]:
            self.parent[u] = self.find(self.parent[u])
            return self.parent[u]
        else:
            return u
    def union(self, u, v):
        up = self.find(u)
        vp = self.find(v)
        if up != vp:
            self.parent[vp] = up
            return False
        else:
            return True

