class Solution {
public:
    bool containsCycle(vector<vector<char>>& grid) {
        int dir[3] = {0, 1, 0}, m = grid.size(), n = grid[0].size();

        vector<int> ds(m * n, - 1);
        function<int(int)> find = [&ds, &find] (int i) {
            return ds[i] < 0 ? i : ds[i] = find(ds[i]);
        };

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                int a = find(i * n + j);
                for (int k = 0; k < 2; k++) {
                    int ni = i + dir[k], nj = j + dir[k + 1];
                    if (ni >= m || nj >= n || grid[i][j] != grid[ni][nj])
                        continue;
                    int b = find(ni * n + nj);
                    if (a == b) return true;
                    ds[b] = a;
                }
            }
        }
        return false;
    }
};
