class Solution:
    def containsCycle(self, grid: List[List[str]]) -> bool:
        # For each character component of the grid, DFS to see if there's a cycle.
        # The constraints are that we can't travel backwards from where we came from.
        # No need to check if the path contains more than 4 elements, because 4 elements is the bare minimum to generate a
        # cycle with these contraints.

        visited = set()

        def hasCycle(y, x, path, prevY, prevX):
            if (y,x) in path:
                return True

            path.add((y,x))
            visited.add((y,x))

            for dy, dx in ((-1,0),(1,0),(0,-1),(0,1)):
                if not (0 <= y+dy < len(grid)) or not (0 <= x+dx < len(grid[0])):
                    continue
                if y+dy == prevY and x+dx == prevX:
                    continue
                if grid[y+dy][x+dx] != grid[y][x]:
                    continue
                if hasCycle(y+dy, x+dx, path, y, x):
                    return True

            return False

        for y in range(len(grid)):
            for x in range(len(grid[0])):
                if (y,x) in visited:
                    continue
                if hasCycle(y,x,set(), None, None):
                    return True

        return False
