class Solution:
    def containsCycle(self, grid: List[List[str]]) -> bool:
        #BFS for same character
        #if find a neighbor node that has been visited before but not previous node, there is a cycle

        #O(n) time and space

        m, n = len(grid), len(grid[0])
        seen = set()
        for i in range(m):
            for j in range(n):
                if (i, j) not in seen:
                    if self.bfs(i, j, grid, seen):
                        return True
        return False

    def bfs(self, i, j, grid, seen):
        m, n = len(grid), len(grid[0])
        q = deque([(i,j,None, None)])
        char = grid[i][j]
        seen.add((i,j))
        while q:
            i, j, pi, pj = q.pop()
            for x, y  in [(1, 0), (0, 1), (-1, 0), (0, -1)]:
                ni, nj = i + x, j + y
                if 0 <= ni < m and 0 <= nj < n and grid[ni][nj] == char:
                    if (ni, nj) in seen:
                        if (ni, nj) != (pi, pj):
                            return True
                    else:
                        q.appendleft((ni, nj, i, j))
                        seen.add((ni, nj))
