class Solution {
    int[][] dirs = {{-1,0},{1,0},{0,1},{0,-1}};
    public boolean containsCycle(char[][] grid) {
        int m = grid.length, n = grid[0].length;
        boolean[][] visited = new boolean[m][n];
        for(int i = 0; i < m; i++)
        {
            for(int j = 0; j < n; j++)
            {
                if(!visited[i][j]&&dfs(grid, i, j, -1, -1, visited, m, n, grid[i][j]))
                {
                    return true;
                }
            }
        }

        return false;
    }

    public boolean dfs(char[][] grid, int i, int j, int prei, int prej, boolean[][] visited, int m, int n, int color)
    {
        if(i<0||j<0||i>=m||j>=n||grid[i][j]!=color)return false;
        if(visited[i][j])return true;
        visited[i][j] = true;
        for(int[] dir : dirs)
        {
            int ni = i + dir[0], nj = j + dir[1];
            if(ni==prei&&nj==prej)continue;
            if(dfs(grid, ni, nj, i, j, visited, m, n, color))return true;
        }

        return false;
    }
}
