class Solution:
    def containsCycle(self, grid: List[List[str]]) -> bool:
        visited=set([])

        moveset=((0,1),(1,0),(-1,0),(0,-1))

        R,C=len(grid),len(grid[0])

        is_cyclic=False

        def traverse(r,c,l,prev_move):

            nonlocal visited,R,C,is_cyclic

            visited.add((r,c))

            if is_cyclic:
                return

            for nextmove in moveset:
                nr=r+nextmove[0]
                nc=c+nextmove[1]
                if 0<=nr<R and 0<=nc<C and grid[nr][nc] == l:
                    if not (nr,nc) in visited and (nr,nc) != prev_move:
                        traverse(nr,nc,l,(r,c))
                    elif  (nr,nc) in visited and (nr,nc) != prev_move:
                        is_cyclic=True
                        return

        for r in range(R):
            for c in range(C):
                if not (r,c) in visited:
                    traverse(r,c,grid[r][c],None)
                    if is_cyclic:
                        return True

        return is_cyclic
