class Solution {
public:
	// Coordinates to be added to current node coordinates to get new coordinates in four directions
    int xPos[4] = {-1, 0, 1, 0};
    int yPos[4] = {0, 1, 0, -1};
	// DFS function returns true if a cycle is present otherwise false
	// Parameters :
	//  - visit : boolean 2d matrix where true means the node is already visited
	//  - x, y : current node coordinates
	//  - m, n : column size and row size
	//  - ch : character value of the current connected graph 
	//  - parX, parY : These are the parent node coordinates
    bool DFS(vector<vector<char>>& grid, vector<vector<bool>>& visit, int x, int y, int m, int n, char ch, int parX, int parY){
		// Mark current node as visited
        visit[x][y] = true;
        int newX, newY;
		// iterate over neighbouring nodes coordinates
        for(int i=0; i<4; i++){
            newX = x + xPos[i];
            newY = y + yPos[i];
			// checking wheather newX and newY coordinates are valid, also the new node
			// should have the same character value of the current connected graph
            if(newX >= 0 && newX < m && newY >= 0 && newY < n && grid[newX][newY] == ch){
                // Now the new Node is a valid one, so we do a DFS traversal if it is not visited already.
			    //  Also the DFS function returns true if it found a cycle. So in that case we immediately return true
				if(!visit[newX][newY]){
                    if(DFS(grid, visit, newX, newY, m, n, ch, x, y))
                        return true;
                }
                else if((parX != newX) || (parY != newY))   
                    return true;
				 // If the node is already visited and the new Node is not equal to the 
				 // parent node(parent node means the previous node in the current DFS path), 
				 // then we can confirm the presence of a cycle
            }
        }
		// If we can't find cycles after completing the DFS of it's neighbours
        return false;
    }

    bool containsCycle(vector<vector<char>>& grid) {
        int m = grid.size(), n = grid[0].size();
        vector<vector<bool>> visit(m, vector<bool>(n, false));
        for(int i=0; i<m; i++){
            for(int j=0; j<n; j++){
                if(!visit[i][j]){
                    if(DFS(grid, visit, i, j, m, n, grid[i][j], -1, -1))
                       return true;
                }
            }
        }
        return false;
    }
};
