General idea for DFS - move in grid only right and down direction from cell that have both neighbours (lower and right) and each DFS will have mark into grid with unique level;

```
class Solution {
public:
  bool containsCycle(vector<vector<char>>& grid){
    int y = grid.size(), x = grid[0].size();
    int yend = y - 1, xend = x - 1, level = 0;
    if(!yend || !xend) return false;
    vector<vector<int>> table(y, vector<int>(x,0));

    for(int yy = 0; yy != yend; yy++)
      for(int xx = 0; xx != xend; xx++){
        if(table[yy][xx]) continue;
        char let = grid[yy][xx];

        stack<pair<int, int>> st;
        table[yy][xx] = ++level;

        if(xx == xend || grid[yy][xx+1] != let || yy == yend || grid[yy+1][xx] != let) continue;

        st.push({yy+1,xx});
        st.push({yy,xx+1});

        while(!st.empty()){
          auto [ty, tx] = st.top(); st.pop();
          table[ty][tx] = level;

          if(ty != yend)
            if(table[ty+1][tx] == level) return true;
            else if(grid[ty+1][tx] == let) st.push({ty+1,tx});

          if(tx != xend)
            if(table[ty][tx+1] == level) return true;
            else if(grid[ty][tx+1] == let) st.push({ty,tx+1});
        }
      }

    return false;
  }
};
```

