        return false;
    }

    private boolean dfs(char[][] grid, int i, int j, char target, int oi, int oj){
        if(i < 0 || i >= m || j < 0 || j >= n) return false;
        if(grid[i][j] != target) return false;
        if(v[i][j]) return true;
        v[i][j] = true;
        boolean cur = false;
        for(int[] d : dirs){
            int x = i + d[0];
            int y = j + d[1];
            if(oi != -1 && oj != -1){
                if(x == oi && y == oj){
                    continue;
                }
            }
            cur |= dfs(grid, x, y, target, i, j);
        }
        return cur;
    }
}
