class Solution {
public:

    vector<vector<bool>> visited;

    bool cycle=false;

    bool isSafe(int i, int j, vector<vector<char>>& grid, char c, int pi, int pj){
        if(i>=0 && j>=0 && i<grid.size() && j<grid[0].size() && grid[i][j]==c){
            if(visited[i][j]==true){
                if(pi==i && pj==j){
                    return false;
                }
                cycle=true;

                return false;
            }
            return true;
        }
        return false;
    }

    void dfs(int i, int j, vector<vector<char>>& grid, int px, int py){
        visited[i][j]=true;


        if(isSafe(i+1, j, grid, grid[i][j], px, py)) dfs(i+1, j, grid, i, j);
        if(cycle) return;

        if(isSafe(i, j+1, grid, grid[i][j], px, py)) dfs(i, j+1, grid, i, j);
        if(cycle) return;

        if(isSafe(i, j-1, grid, grid[i][j], px, py)) dfs(i, j-1, grid, i, j);
        if(cycle) return;

        if(isSafe(i-1, j, grid, grid[i][j], px, py)) dfs(i-1, j, grid, i, j);
        if(cycle) return;


    }

    bool containsCycle(vector<vector<char>>& grid) {

        int m = grid.size();
        int n = grid[0].size();
        visited.resize(m);

        for(int i=0;i<m;i++){
            visited[i].resize(n,0);
        }

        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(visited[i][j]==false){
                    dfs(i,j,grid, -1, -1);
                    if(cycle)
                        return true;
                }
            }
        }

      /*  
        a b b
        b z b
        b b a
        */


        return false;

    }
};
