class Solution {
public:
	bool ans;
	vector<int> dir{1,0,-1,0,1};
	void dfs(vector<vector<char>>& grid, vector<vector<int>>& visited, int x,int y,int lastX,int lastY,char cur,int color){
		visited[x][y]=color;
		for(int i=0;i<4;i++){
			int a=x+dir[i];
			int b=y+dir[i+1];
			if(a<0 || b<0 || a>=grid.size() || b>=grid[0].size()) continue;
			if(!(a==lastX && b==lastY) && grid[a][b]==cur && visited[a][b]==color){
				ans=true;
			}
			if(visited[a][b]==0 && !(a==lastX && b==lastY) && grid[a][b]==cur){
				dfs(grid,visited,a,b,x,y,cur,color);
			}
		}
	}
	bool containsCycle(vector<vector<char>>& grid) {
		ans=false;
		int m=grid.size();
		int n=grid[0].size();
		vector<vector<int>> visited(m,vector<int>(n));
		int color=1;
		for(int i=0;i<m;i++){
			for(int j=0;j<n;j++){
				if(visited[i][j] ==0){
					dfs(grid,visited,i,j,-1,-1,grid[i][j],color);
					color++;
				}
			}
		}

		return ans;
	}
};

