class Solution {
public:
    int m;
    int n;
    bool bRet = false;
    void floodfill(vector<vector<char>>& grid, vector<vector<int>>& path, int i, int j, int parent_i, int parent_j, char target)
    {
        if(bRet) return;
        if(path[i][j] == 1) bRet = true;

        path[i][j]=1;

        if(i-1>=0 && grid[i-1][j] ==target && (i-1!=parent_i))floodfill(grid, path, i-1, j, i, j, target);
        if(i+1<m && grid[i+1][j] ==target && (i+1 != parent_i))floodfill(grid, path, i+1, j, i, j, target);
        if(j-1>=0 && grid[i][j-1] ==target && (j-1 !=parent_j))floodfill(grid, path, i, j-1, i, j, target);
        if(j+1<n && grid[i][j+1] ==target && (j+1 !=parent_j))floodfill(grid, path, i, j+1, i, j, target);
    }
    bool containsCycle(vector<vector<char>>& grid) {
        m = grid.size();
        n = grid[0].size();
        vector<vector<int>> path(m, vector<int>(n, 0));
        for(int i=0; i<m; i++)
        {
            for(int j=0; j<n; j++)
            {
                bRet = false;
                if(path[i][j]==0) floodfill(grid, path, i, j, -1, -1, grid[i][j]);
                if(bRet) return true;
            }
        }

        return false;

    }
};
```

