class Solution {
    public boolean containsCycle(char[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        boolean[][] visited = new boolean[m][n];
        boolean res = false;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(!visited[i][j]){
                    res = res || dfs(i, j, grid, visited, m, n, -1, -1);
                }
            }
        }
        return res;
    }

    boolean dfs(int i, int j, char[][] grid, boolean[][] visited, int m, int n, int pi, int pj){
        if(visited[i][j]) return true;
        visited[i][j] = true;
        boolean res = false;
        if(i>0 && grid[i-1][j]==grid[i][j] && i-1!=pi){
            res = res || dfs(i-1, j, grid, visited, m ,n, i, j);
        }
        if(i<m-1 && grid[i+1][j]==grid[i][j] && i+1!=pi){
            res = res || dfs(i+1, j, grid, visited, m ,n, i, j);
        }
        if(j>0 && grid[i][j-1]==grid[i][j] && j-1!=pj){
            res = res || dfs(i, j-1, grid, visited, m ,n, i, j);
        }
        if(j<n-1 && grid[i][j+1]==grid[i][j] && j+1!=pj){
            res = res || dfs(i, j+1, grid, visited, m ,n, i, j);
        }
        return res;
    }
}
