from collections import deque
class Solution:
    def containsCycle(self, grid: List[List[str]]) -> bool:
        s={}

        m=len(grid)
        n=len(grid[0])

        def helper(i,j):
            char=grid[i][j]
            q=deque()
            q.append((i,j,-1,-1))
            while(q):
                x,y,c,d=q.popleft()
                if(0<=x<m and 0<=y<n and grid[x][y]==char):
                    if((x,y) not in s):
                        s[(x,y)]=(c,d)
                        if(x-1,y)!=(c,d):
                            q.append((x-1,y,x,y))
                        if(x,y+1)!=(c,d):
                            q.append((x,y+1,x,y))
                        if(x+1,y)!=(c,d):
                            q.append((x+1,y,x,y))
                        if(x,y-1)!=(c,d):
                            q.append((x,y-1,x,y))
                    elif(s[(x,y)]!=(c,d)):
                        return True
            return False



        for i in range(0,m):
            for j in range(0,n):
                if((i,j) not in s):
                    if(helper(i,j)):
                        return True
        return False
