# Code for BFS
```

bool detectCycleBFS(vector<vector<int>>& vis, vector<vector<char>>& grid, int row, int col){
    vis[row][col] = 1;
    // current_node {} parent_node {}
    queue<pair<pair<int,int>, pair<int,int>>> q;
    q.push({{row,col},{-1,-1}});
    while(!q.empty()){
        int row = q.front().first.first;
        int col = q.front().first.second;
        int Prow = q.front().second.first;
        int Pcol = q.front().second.second;
        q.pop();

        for(int drow = -1; drow<=1; drow++){
            for(int dcol = -1; dcol<=1; dcol++){
                if(drow!=0 && dcol!=0 || (drow==0 && dcol==0)){
                    continue;
                }
                int nrow = drow + row;
                int ncol = dcol + col;

                if(nrow>=0 && nrow<grid.size() && ncol>=0 && ncol<grid[0].size() && grid[nrow][ncol] == grid[row][col]){
                    if(!vis[nrow][ncol]){
                        vis[nrow][ncol] = 1;
                        q.push({{nrow,ncol},{row,col}});
                    }else if(nrow != Prow && ncol != Pcol){ // visited and NOT parent
                    return true;
                    }
                }
            }
        } 
    }
    return false;
}
```

