class Solution {
public:
    bool bfs(int row, int col, vector<vector<char>>& grid, vector<vector<int>>& vis, int& count, char c){
        queue<vector<int>> q;
        q.push({row, col, row, col});
        int n = grid.size(), m = grid[0].size();
        vis[row][col] = 1;
        int dx[] = {-1, 0, 1, 0};
        int dy[] = {0, 1, 0, -1};
        while(!q.empty()){
            auto vec = q.front();q.pop();
            int row = vec[0];
            int col = vec[1];
            int rp = vec[2];
            int cp = vec[3];

            for(int i=0;i<4;i++){
                int nrow = row + dx[i];
                int ncol = col + dy[i];
                if(nrow >= 0 and nrow < n and ncol >= 0 and ncol < m and grid[nrow][ncol] == c){
                    if(vis[nrow][ncol] and nrow != rp and ncol != cp) return true;
                    else if(!vis[nrow][ncol]){
                        vis[nrow][ncol] = 1;
                        q.push({nrow, ncol, row, col});
                        count++;
                    }
                }
            }
        }
        return false;
    }
    bool containsCycle(vector<vector<char>>& grid) {
        int n = grid.size(), m = grid[0].size();
        vector<vector<int>> vis(n, vector<int>(m, 0));

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(!vis[i][j]){
                    int count = 1;
                    if(bfs(i, j, grid, vis, count, grid[i][j]) == true and count >= 4) return true;
                }
            }
        }
        return false;
    }
};
