class Solution {
private:
    bool dfs(int r, int c, int pr, int pc, vector<vector<char>>&grid, vector<vector<int>>& vis){
        int m = grid.size(), n = grid[0].size();
        vis[r][c] = 1;

        int dx[4] = {-1, 0, 1, 0};
        int dy[4] = {0, -1, 0, 1};

        for(int i = 0;i < 4;i++){
            int nx = r + dx[i], ny = c + dy[i];
            if(nx < 0 || nx >= m || ny < 0 || ny >= n || grid[r][c] != grid[nx][ny]) continue;
            if(nx == pr && ny == pc) continue;
            if(vis[nx][ny]){
                return true;
            }
            else if(dfs(nx, ny, r, c, grid, vis)) 
                return true;
        }
        return false;
    }
public:
    bool containsCycle(vector<vector<char>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        vector<vector<int>> vis(m, vector<int>(n, 0));
        for(int i = 0;i < m;i++){
            for(int j = 0;j < n;j++){
                if(!vis[i][j]){
                    if(dfs(i, j, -1, -1, grid, vis))
                        return true;
                }
            }
        }
        return false;

    }
};
