class Solution {
private:
    bool insideGrid(int i,int j,int m,int n){
        return i>=0 and i<m and j>=0 and j<n;
    }
    bool dfs(vector<vector<char>>& grid, vector<vector<bool>>& vis, int row, int col,int pr,int pc) {

        char c = grid[row][col];
        int m = grid.size();
        int n = grid[0].size();

        if (vis[row][col] ) {
            return true;
        }

        vis[row][col] = true;

        int pos[4][2] = {{-1, 0}, {0, -1}, {1, 0}, {0, 1}};

        for (int i = 0; i < 4; i++) {
            int childI = row + pos[i][0];
            int childJ = col + pos[i][1];

            if (insideGrid(childI,childJ,m,n) and grid[childI][childJ] == c and(childI!= pr or childJ!=pc)) {
                if(dfs(grid,vis,childI,childJ,row,col)){
                    return true;
                }
            }
        }


        return false;
    }
public:
    bool containsCycle(vector<vector<char>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        vector<vector<bool>> vis(m, vector<bool>(n, false));

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (!vis[i][j]) {
                    if (dfs(grid, vis, i, j,-1,-1)) {
                        return true;
                    }
                }
            }
        }

        return false;
    }
};

