class Solution {
public:
    bool solve(int i,int j,char a,vector<vector<char>>& grid,vector<vector<int>>& vis,int pari,int parj){
      vis[i][j]=1;
      int delr[]={0,0,-1,1};
      int delc[]={1,-1,0,0};
      for(int k=0;k<4;k++){
          int nr=delr[k]+i;
          int nc=delc[k]+j;
          if(pari==nr&&parj==nc) continue;
          if(nr>=0&&nr<grid.size()&&nc>=0&&nc<grid[0].size()&&grid[nr][nc]==a&&vis[nr][nc]==1){
              return true;
          }
          else if(nr>=0&&nr<grid.size()&&nc>=0&&nc<grid[0].size()&&grid[nr][nc]==a&&vis[nr][nc]==0){
              if(solve(nr,nc,a,grid,vis,i,j)==true) return true;
          }
      }
      return false;
    }
    bool containsCycle(vector<vector<char>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        vector<vector<int>> vis(n,vector<int> (m,0));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                char ch=grid[i][j];
                if(vis[i][j]==0&&solve(i,j,ch,grid,vis,-1,-1)==true){
                 return true;
                }
            }
        }
        return false;
    }
};
