class Solution {
public:

    bool solvedfs(vector<vector<char>> &grid,vector<vector<int>> &visited,int i,int j,int p_r,int p_c,int n,int m,int len)
    {

        visited[i][j] = true;
        len++;

        int ar[4]={0,-1,0,1};
        int br[4]={-1,0,1,0};

        for(int k=0;k<4;k++)
        {
            int r = i+ar[k];
            int c = j+br[k];

            if(r <0 || c<0 ||r>=n ||c>=m || grid[r][c] != grid[i][j])
            {
                continue;
            }
            if(r==p_r && c==p_c)
