            if(!visited[r][c])
            {
                bool ans = solvedfs(grid,visited,r,c,i,j,n,m,len);
                if(ans)
                    return true;
            }
            else
            {
                if( (r != p_r || c!=p_c) && len >= 4)
                    return true;
